import pytest
from unittest import mock
import builtins


def company_logo():
    chars = input('')
    dict = {}
    for letter in chars:
        if letter in dict:
            dict[letter] += 1
        else:
            dict[letter] = 1
    sorted_list = sorted(dict.items(), key=lambda x: x[0])
    sorted_list.sort(reverse=True, key=lambda x: x[1])
    sorted_list = sorted_list[:3]
    return sorted_list


def test_company_1():
    with mock.patch.object(builtins, 'input', lambda _: 'aabbbccde'):
        assert company_logo() == [('b', 3), ('a', 2), ('c', 2)]

def test_company_2():
    with mock.patch.object(builtins, 'input', lambda _: 'qwertyuiopasdfghjklzxcvbnm'):
        assert company_logo() == [('a', 1), ('b', 1), ('c', 1)]

def test_company_3():
    with mock.patch.object(builtins, 'input', lambda _: ''):
        assert company_logo() == [('a', 419), ('w', 416), ('y', 415)]

