import re
import pytest
from unittest import mock
import builtins

def mutate_string():
    string = input('')
    s=input('')
    i, c = s.split()
    lists = list(string)
    lists[int(i)] = c
    return "".join(lists)

def test_mutations_1():
    with mock.patch.object(builtins, 'input', side_effect=[ 'abracadabra', '5 k']):
        assert mutate_string() == 'abrackdabra'


def test_mutations_2():
    with mock.patch.object(builtins, 'input', side_effect=[
        '', '27 p']):
        assert mutate_string() == ''


